let carrito=[];let total=0;function agregarAlCarrito(nombre,precio){carrito.push({nombre:nombre,precio:precio});total+=precio;actualizarCarrito()}function actualizarCarrito(){const carritoDiv=document.getElementById("carrito");const totalSpan=document.getElementById("total");carritoDiv.innerHTML="";carrito.forEach(((producto,index)=>{const productoDiv=document.createElement("div");productoDiv.innerHTML=`\n      <span>${producto.nombre}</span>\n      <span>$${producto.precio.toLocaleString()} COP</span>\n      <button onclick="eliminarDelCarrito(${index})">X</button>\n    `;carritoDiv.appendChild(productoDiv)}));totalSpan.textContent=`$${total.toLocaleString()} COP`}function eliminarDelCarrito(index){total-=carrito[index].precio;carrito.splice(index,1);actualizarCarrito()}function vaciarCarrito(){carrito=[];total=0;actualizarCarrito()}function toggleCarrito(){const carritoLateral=document.getElementById("carrito-lateral");carritoLateral.classList.toggle("active")}